
public class typecasting {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
byte b = 20;
int i = b;
byte d = (byte)i;

byte e = 20;
byte f = 6;
int g = e*f;
System.out.println("the result is:" +g);
int x=15;
int y=10;
byte z=(byte)(x*y);
System.out.println(z);
	}

}
